import { takeLatest, call, all } from 'redux-saga/effects';

import {auth, googleProvider, createUserProfileDocument} from '../../firebase/Firebase.Utils';

//import this action seperately bcz yield put(fetchCollectionsStart) can't be called inside takeEvery
import { 
    GOOGLE_SIGN_IN_START,
    EMAIL_SIGN_IN_START,
} from '../actions/types';

 import {
    googleSignInSuccess, googleSignInFailure,
    emailSignInSuccess, emailSignInFailure
 } from './userActions';


 export function* signInWithGoogle() {
    try {
        const userObj = yield auth.signInWithPopup(googleProvider);
        //destruct user from user objects data
        const { user } = userObj ;
        const userRef = yield (createUserProfileDocument, user)
        console.log(userRef);
    } catch (error) {
        
    }
 }
// yield put(fetchCollectionsStart()); can't be called inside yield takeEvery
export function* onGoogleSignInStart(){
    yield takeLatest(
        GOOGLE_SIGN_IN_START, 
        signInWithGoogle
    )
}

export function* userSagas(){
    yield all([call(onGoogleSignInStart)])
}
