import {
    SET_CURRENT_USER_NULL,
    SET_CURRENT_USER
} from '../actions/types'

const INITIAL_STATE = {
    currentUser: null
}

const userReducer = (state = INITIAL_STATE, action) => {
    switch(action.type) {
        case SET_CURRENT_USER_NULL:
            return {
                state, 
                currentUser: action.payload
            }
        case SET_CURRENT_USER:
            return {
                state, 
                currentUser: action.payload
            }
        default:
            return state;    
    }
}

export default userReducer;
