import React from 'react';
import { connect } from 'react-redux';

import './collectionItems.styles.scss';
import CustomButton from '../../sharing-custom.button/CustomButton';
import { addItem } from '../../../redux/actions/cartActions'

const CollectionItem = ({item, addItem}) => {
  //destruct props from item props
   const {name, price, imageUrl} = item
  return (
    <div className='collection-item '>
      <div
        className='image'
        style={{
          backgroundImage: `url(${imageUrl})`
        }}
      />
      <div className='collection-footer'>
        <span className='name'>{name}</span>
        <span className='price'>{price}</span>
      </div>
      <CustomButton  onClick={()=> addItem(item)} >
        Add to cart
      </CustomButton>
    </div>
  );
      }

      const mapStateToProps = ({cart: {cartItems}}) => ({
        cartItem: state.cart.cartItem
      })
export default connect(mapStateToProps, {addItem})(CollectionItem);
