import React from 'react';
import { connect } from 'react-redux';

import { ReactComponent as ShoppingCartItem } from '../../assets/ShoppingCartIcon.svg';
import { cartToggleHidden } from '../../redux/actions/cartActions'


import './cartIcon.styles.scss';

const CartIcon = ({cartToggleHidden}) => {
    return (
        <div className='cart-icon'>
            <ShoppingCartItem className='shopping-icon ' onClick= {cartToggleHidden} />
            <span className='item-count'>0</span>
        </div>
    )
}

const mapDispatchToProps = dispatch =>  ({
    cartToggleHidden : () => dispatch(cartToggleHidden)
})
export default connect(null, mapDispatchToProps)(CartIcon)
