import React, {useEffect } from 'react';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux';

import CollectionOverview from '../../collections/collection-overview/CollectionOverview';
import SubCollection from '../../collections/sub-Collection/SubCollection';

import { createStructuredSelector } from 'reselect';
import {selectIsCollectionsLoaded } from '../../../redux/shop/shopSelectors'
import { fetchCollectionsStart } from '../../../redux/shop/shopActions' 


const ShopPage = ({ match, fetchCollectionsStart, selectIsCollectionsLoaded }) =>{
  useEffect(() => {
    fetchCollectionsStart();
    //eslint-disable-next-line
  }, [])

  return (
  <div className='shop-page'>
    <Route exact path={`${match.path}`} component={CollectionOverview}  />
    <Route path={`${match.path}/:collectionId`} component={SubCollection} isLoading={selectIsCollectionsLoaded} />
  </div>
);
}


const mapStateToProps = createStructuredSelector ({
  selectIsCollectionsLoaded: selectIsCollectionsLoaded
})

//send action direct from comp in useEffect
const mapDispatchToProps = dispatch => ({
  fetchCollectionsStart: () => dispatch(fetchCollectionsStart()) 
})
export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
