import  { createSelector } from 'reselect';

const selectCard = state => state.cart;

export const selectCardItems =  creaceSelector(
    [selectCard],
    cart => cart.cartItems
);

export const selectCardItemsCountitem = createSelector(
    [selectCardItems],
    countItem => state.cart.cartItems.reduce((total, cardItem) => total + cardItem.quantity, 0)
)
