import React from 'react'
import './App.css';
import { BrowserRouter, Switch, Route, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';



import { setCurrentUser } from './redux/user/userActions'

import HomePage from './components/pages/homepage-folder/HomePage';
import ShopPage from './components/pages/shop/ShopPage';
import ContactPage from './components/pages/contact-page/ContactPage';
import SignInAndSignUpPage from './components/sign-in-And-sign-up/sign-in-And-sign-up/SignInAndSignUp'
import CheckOutPage from './components/pages/check-Out/CheckOutPage';
import Header from './components/pages/header/Header';

import {auth, createUserProfileDocument } from './firebase/Firebase.Utils'


class App extends React.Component {

  unsubscribeFromAuth = null;


  componentDidMount() {
 /*   //destructuring props from states in userReducer
    const { setCurrentUser } = this.props;


    //Firebase //signing in the user by         
    this.unsubscribeFromAuth = auth.onAuthStateChanged(async userAuth => {
      if (userAuth) {
        const userRef = await createUserProfileDocument(userAuth);

        //snapshot is user data which is sent back from firesbase backend
        userRef.onSnapshot(snapShot => {
          setCurrentUser({
              id: snapShot.id,
              ...snapShot.data()
          });
        });
      }

      //setting snapshot user obj to userAuth
      setCurrentUser( userAuth );

    }); */
  }

  componentWillUnmount() {
    //signing out the user by calling unsubscribeFromAuth method
    this.unsubscribeFromAuth();
  }

  render() {
    return (
        <div className='App'>
          <BrowserRouter>
            <Header />
              <Switch>
                <Route exact path='/' component={HomePage} />
                <Route path='/shop' component={ShopPage} />
                <Route path='/contact' component={ContactPage} />
                <Route exact path='/signin' 
                  render={ ()=> this.props.currentUser ? (<Redirect to='/' />) : (<SignInAndSignUpPage />) } 
                />
                <Route path='/checkout' component={CheckOutPage} />
              </Switch>
          </BrowserRouter>
        </div>
    )
  }
}

const mapStateToProps = state => ({
  currentUser: state.user.currentUser
})

const mapDispatchToProps = dispatch => ({
  // explicitly forwarding arguments, sending action directly from here,  writing user is option, this disp user comes from snapShot
  setCurrentUser: user => dispatch(setCurrentUser(user))
});
export default connect(mapStateToProps, mapDispatchToProps)(App);
