import { 
    TOGGLE_CART_HIDDEN,
    ADD_ITEM
} from "./types"

/*
export const cartToggleHidden = () => ({
    type: TOGGLE_CART_HIDDEN
})
*/
// Set loading to true
export const cartToggleHidden = () => {
    return {
      type: TOGGLE_CART_HIDDEN
    };
  };

//Add item to cart
export const addItem = item  => ({
        type: ADD_ITEM,
        payload: item
})
//Remove item from cartItems
export const removeItem = item  => ({
  type: ADD_ITEM,
  payload: item
})
