import {
    SET_CURRENT_USER,
    GOOGLE_SIGN_IN_START,
    GOOGLE_SIGN_IN_SUCCESS,
    GOOGLE_SIGN_IN_FAILURE,
    EMAIL_SIGN_IN_START,
    EMAIL_SIGN_IN_SUCCESS,
    EMAIL_SIGN_IN_FAILURE
} from '../actions/types'


export const setCurrentUser = user => ({
    type: SET_CURRENT_USER,
    payload: user
  });

  //FOR GOOGLE SIGN IN ACTIONS
  export const googleSignInStart = () => dispatch => {
      return {
        type: GOOGLE_SIGN_IN_START
      }
  }

  export const googleSignInSuccess = user => dispatch => {
    return {
        type: GOOGLE_SIGN_IN_SUCCESS,
        payload: user
    }
  }

  export const googleSignInFailure = error => dispatch => {
    return {
        type: GOOGLE_SIGN_IN_FAILURE,
        payload: error
    }
  }

  //FOR EMAIL SING IN ACTIONS
  export const emailSignInStart = () => dispatch => {
    return {
      type: GOOGLE_SIGN_IN_START
    }
}

export const emailSignInSuccess = user => dispatch => {
  return {
      type: GOOGLE_SIGN_IN_SUCCESS,
      payload: user
  }
}

export const emailSignInFailure = error => dispatch => {
  return {
      type: GOOGLE_SIGN_IN_FAILURE,
      payload: error
  }
}
