import {
    SET_CURRENT_USER_NULL,
    SET_CURRENT_USER
} from './types'



//set user null
export const setCurrentUserNull = dispatch => ({
    type: SET_CURRENT_USER_NULL
  });

  //set current user
export const setCurrentUser = user => dispatch => {
    dispatch({
        type: SET_CURRENT_USER,
        payload: user
    })
  };
