import { createSelector } from 'reselect';

//Matchs string values to numeric id 
const COLLECTION_MAP_ID = {
    hats: 1,
    sneakers: 2,
    jackets: 3,
    womens: 4,
    men: 5
}

//TWO TYPES OF SELECTOR FOR MEMOIZATION VALUE
//this is input selector, it not uses state, it gives back a piece of state
const selectShop = state => state.shop;

export const selectShopCollection =  createSelector(
    [selectShop],
    shop => shop.collections
);

//Match collection.id to collection.url params
export const selectCollectionMatch = collectionUrlParams = createSelector(
    [selectShopCollection],
    collections => collections.map(collection => collection.id === collectionUrlParams[COLLECTION_MAP_ID]) 
)
