import React, {useEffect } from 'react';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux';

import CollectionOverview from '../../collections/collection-overview/CollectionOverview';
import SubCollection from '../../collections/sub-Collection/SubCollection';

import { createStructuredSelector } from 'reselect';
import {selectIsCollectionFetching } from '../../../redux/reducers/selectors-And-card.utils/shopSelectors'
import { fetchCollectionsStartAsync } from '../../../redux/actions/shopActions'


const ShopPage = ({ match, fetchCollectionsAsync }) =>{
  useEffect(() => {
    fetchCollectionsAsync();
    //est-lint-diable-next-line
  }, [])

  return (
  <div className='shop-page'>
    <Route exact path={`${match.path}`} component={CollectionOverview} />
    <Route path={`${match.path}/:collectionId`} component={SubCollection} />
  </div>
);
}


//send action direct from comp
const mapStateToProps = createStructuredSelector ({
  isCollectionFetching: selectIsCollectionFetching  
})

const mapDispatchToProps = dispatch => ({
  fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync()) 
})
export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
