import React, {useState} from 'react';

import FormInput from '../form-input/FormInput'
import CustomButton from '../../sharing-custom.button/CustomButton'
import './signUp.styles.scss'

import { auth, createUserProfileDocument }  from '../../../firebase/Firebase.Utils';

const SignUp = () => {
  //instead of having 4 different states we can simply have one state 'contact
    const [contact, setContact] = useState({
        name: '',
        email: '',
        password: '',
        confirmPassword: ''
    });


    //pulling out values from contact
    const {name, email, password, confirmPassword} = contact
    const handleChange = (e) => {
        setContact({ ...contact, [e.target.name]: e.target.value})
    }
    //define onSubmit
    const onSubmit = async e => {
        e.preventDefualt();

        //check if pwds not match
        if(password !== confirmPassword) {
            alert('Psswords dont match');
            return;
        }

        try {
            const { user } = await auth.createUserWithEmailAndPassword(
                email,
                password
            );
        
            await createUserProfileDocument(user, { displayName });
        console.log(user);
                setDisplayName('');
                setEmail('');
                setPassword('');
                setConfirmPassword('');
                
            } catch (error) {
            console.error(error);
            }

    }


    
    return (
        <div className='sign-up'>
            <h2 className='title'>I don't have an account</h2>
            <span>Sign in with your email and password</span>

            <form onSubmit={onSubmit}>
                <FormInput
                type='text'
                name='displayName'
                value={displayName}
                onChange={handleChange}
                label='Display Name'
                required
            />
            <FormInput
                type='email'
                name='email'
                value={email}
                onChange={handleChange}
                label='Email'
                required
            />
            <FormInput
                type='password'
                name='password'
                value={password}
                onChange={handleChange}
                label='Password'
                required
            />
            <FormInput
                type='password'
                name='confirmPassword'
                value={confirmPassword}
                onChange={handleChange}
                label='Confirm Password'
                required
            />
                <div className='buttons'>
                    <CustomButton type='submit'>Sign Up</CustomButton>                   
                </div>
            </form>
        </div>
    );
}

export default SignUp;
