import { call, all } from 'redux-saga/effects';

import { fetchCollectionsStart } from './shop/shopSagas';
import { onGoogleSignInStart, onEmailSignInStart } from './user/userSagas';

export default function* rootSaga(){
    yield all([call(fetchCollectionsStart), call(onGoogleSignInStart), call(onEmailSignInStart)])
}
