import React, {useEffect} from 'react';
import { Route } from 'react-router-dom';


import CollectionOverview from '../../collections/collection-overview/CollectionOverview';
import SubCollection from '../../collections/sub-Collection/SubCollection';

import firebase from '../../../firebase/Firebase.Utils'

useEffect(() => {
  const collectionRef = firestore.collection(collections);

  collectionRef.onSnapshot(snapShot => {
    convertCollectionsSnapshotToMap(snapShot) 
  })
}, [input])

const ShopPage = ({ match }) => (
  <div className='shop-page'>
    <Route exact path={`${match.path}`} component={CollectionOverview} />
    <Route path={`${match.path}/:collectionId`} component={SubCollection} />
  </div>
);

export default ShopPage;
