import { 
    TOGGLE_CART_HIDDEN,
    ADD_ITEM ,
    REMOVE_ITEM 
    } from "../actions/types"

    //import carItemUtils func
    import { addItemToCart } from './card.utils/cartItems.Utils'
    
const INITIAL_STATE = {
    hidden: true,
    cartItems: []
}

const cartReducer = (state= INITIAL_STATE, action) => {
    switch(action.type){
        case TOGGLE_CART_HIDDEN:
            return {
                ...state,
                hidden: !state.hidden
            }
            case ADD_ITEM:
                return {
                    ...state,
                    //addItemToCart func comes from cartItems.utils file
                    cartItems: addItemToCart( state.cartItems, action.payload), 
                    loading: false
                };
            case REMOVE_ITEM:
                return{
                    ...state,
                    cartItems: state.cartItems.filter(cartItem => cartItem.id !== action.payload)
                }    
        default: 
            {
                return state
            }    
    }
}

export default cartReducer;
