import { createSelector } from 'reselect';



//TWO TYPES OF SELECTOR FOR MEMOIZATION VALUE
//this is input selector, it not uses state, it gives back a piece of state
const selectShop = state => state.shop;

export const selectShopCollection =  createSelector(
    [selectShop],
    shop => shop.collections
);

//Match collection.id to collection.url params

export const selectCollectionMatch = collectionUrlParam =>
  createSelector(
    [selectShopCollection],
    collections => collections[collectionUrlParam]
  );
