import { selectCollectionUrlMatch, selectIsCollectionsLoaded } from '../../../redux/shop/shopSelectors'

const SubCollection = ({ collection, isCollectionsLoaded }) => {
  const { title, items } = collection;

  if (isCollectionsLoaded || collection.length > 1 ) {
    return <Spinner />
  }
  
