import { type } from "os"

const INITIAL_STATE = {
    hidden: true
}

const cartReducer = (state= INITIAL_STATE, action) => {
    switch(type.action){
        case TOGGLE_CART_HIDDEN:
            return {
                ...state,
                hidden: !state.hidden
            }
    }
}

export default cartReducer;
