        <Fragment>
            <img
            src={spinner}
            style={{ width: '200px', margin: 'auto', display: 'block' }}
            alt='Loading...'
            />
      </Fragment>
    ) : (
      <WrappedComponent {...otherProps} />
    );
 // };
 // return Spinner;
};

