import React from 'react'
import { connect } from 'react-redux';

import { removeItemFromCart } from '../../../../redux/actions/cartActions'
import './checkOutItems.styles.scss';

const CheckOutItems = ({cartItem, removeItem }) => {
    //dextructure from cartItem props
    const { imageUrl, name, quantity, price} = cartItem
    return (
        <div className='checkout-items'>
            <div className='image-container'>
                <img src={imageUrl} alt='item' />
            </div>
            <div className='name'>{name}</div>
            <div className='quantity'>{quantity}</div>
            <div className='price'>{price}</div>
            <div className='remove' onClick={() => removeItem(cartItem)}>X</div>
        </div>
    )
}

const mapDispatchToProps = dispatch => ({
    removeItem: item => dispatch(removeItemFromCart(item))
})

export default connect(mapDispatchToProps)(CheckOutItems)
