import React from 'react'
import { connect } from 'react-redux';

import './subCollection.styles.scss';

import CollectionItems from '../collection-preview-items/CollectionItems'

import { selectCollectionMatch } from '../../../redux/reducers/selectors-And-card.utils/shopSelectors'

const SubCollection = ({collection}) => {
    //destruct
    const { title, items } = collection;

    return (
        <div className='sub-collection'>
             {items
            .filter((item, idx) => idx < 4)
            .map(item => (
                <CollectionItems key={item.id} item={item} />
          ))}
        </div>
    )
}


//selectCollectionMatch is for nested routing
const mapStateToProps = (state, ownProps) => ({
    collection: selectCollectionMatch(ownProps.match.params.collectionId)(state)
  })
export default connect(mapStateToProps)(SubCollection);
