import React from 'react';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux';

import CollectionOverview from '../../collections/collection-overview/CollectionOverview';
import SubCollection from '../../collections/sub-Collection/SubCollection';

import { selectCollectionMatch } from '../../../redux/reducers/selectors-And-card.utils/shopSelectors'


const ShopPage = ({ match}) => {

    return (
      <div className='shop-page'>
        <Route exact path={`${match.path}`} component={CollectionOverview} />
        <Route exact path={`${match.path}/:SubCollectionId`} component={SubCollection} />
      </div>
    );
}

const mapStateToProps = (state, ownProps) => ({
  collection: selectCollectionMatch(ownProps.match.params)(state)
})
export default connect()(ShopPage);
