import React, {useEffect, useState} from 'react';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux';

import CollectionOverview from '../../collections/collection-overview/CollectionOverview';
import SubCollection from '../../collections/sub-Collection/SubCollection';


import { fetchCollectionsAsync } from '../../../redux/actions/shopActions'


const ShopPage = ({ match, fetchCollectionsAsync }) =>{
  useEffect(() => {
    fetchCollectionsAsync()
  }, [])

  return (
  <div className='shop-page'>
    <Route exact path={`${match.path}`} component={CollectionOverview} />
    <Route path={`${match.path}/:collectionId`} component={SubCollection} />
  </div>
);
}


//send action direct from comp
const mapStateToProps = createStructuredSelector ({
  isCollectionFetching: selectIsCollectionFetching  
})

const mapDispatchToProps = () => ({
  fetchCollectionsAsync: () => dispatch(fetchCollectionsAsync) 
})
export default connect(null, mapDispatchToProps)(ShopPage);
