import React, {useEffect, useState} from 'react';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux';

import CollectionOverview from '../../collections/collection-overview/CollectionOverview';
import SubCollection from '../../collections/sub-Collection/SubCollection';

import {firestore, convertCollectionsSnapshotToMap } from '../../../firebase/Firebase.Utils'

import { collectionsFromFirebaseDB } from '../../../redux/actions/shopActions'


const ShopPage = ({ match, updateCollections }) =>{
  //const [unSubscribeFromSnabshot, setUnSubscribeFromSnabshot] = useState(null);
  const unSubscribeFromSnabshot = null;
  useEffect(() => {
    //fetch collection data from backend
    const collectionRef = firestore.collection('collections');
  
    collectionRef.onSnapshot(snapShot => {
      const collectionsMap = convertCollectionsSnapshotToMap(snapShot); 
      updateCollections(collectionsMap) ;
    })
  }, [])

  return (
  <div className='shop-page'>
    <Route exact path={`${match.path}`} component={CollectionOverview} />
    <Route path={`${match.path}/:collectionId`} component={SubCollection} />
  </div>
);
}

const mapDispatchToProps = state => ({
  updateCollections: collectionsMap => dispatch(collectionsFromFirebaseDB(collectionsMap))  
})
export default connect(null, mapDispatchToProps)(ShopPage);
